mapper.select:
select * from festivaldata
where #{num} 
order by num desc


   select num, title, address, firstimg, startdate, enddate, mapx, mapy, mlevel
from(
   select
   rownum rn, num, title, address, firstimg, startdate, enddate, mapx, mapy, mlevel
   from festivaldata
   where ROWNUM <= #{pageNum}*#{amount}
)
where





rn > (#{pageNum}-1)*#{amount}
